<?php//// Áàçîâûé êîíòðîëëåð ñàéòà.//abstract class C_Base extends C_Controller{	protected $title;		// çàãîëîâîê ñòðàíèöû	protected $content;		// ñîäåðæàíèå ñòðàíèöû	//	// Êîíñòðóêòîð.	//	function __construct()	{			}		protected function before()	{		$this->title = 'Интернет-магазин';		$this->content = '';	}		//	// Ãåíåðàöèÿ áàçîâîãî øàáëîíàû	//		public function render()	{		$vars = array('title' => $this->title, 'content' => $this->content);			$page = $this->Template('v/v_main.php', $vars);						echo $page;	}	}