<?phpsession_start();//// Конттроллер страницы чтения.//include_once('m/model.php');class C_Page extends C_Base{	//	// Конструктор.	//		public function action_index(){		$this->title .= ':  Главная страница';		$text = text_get();		$this->content = $this->Template('v/v_index.php', array('text' => $text));		}		public function action_edit(){		$this->title .= ': Регистрация';				if($this->isPost())		{			text_set($_POST['text']);			header('location: index.php');			exit();		}				$text = text_get();		$this->content = $this->Template('v/v_edit.php', array('text' => $text));			}		public function action_private(){		$this->title .= ':  Личный кабинет';		$text = text_get();		$this->content = $this->Template('templates/private.tpl', array('text' => $text));		}	public function action_cart(){		$this->title .= ':  Корзина';		$text = text_get();		$this->content = $this->Template('templates/cart.tpl', array('text' => $text));		}	public function action_test(){		$this->title .= ':  Тест';		$text = text_get();		if (isset($_POST['login'])){     		       		$login = $_POST['login'];      		if ($login == ''){       		unset($login);    }   } //заносим введенный пользователем логин в переменную $login, если он пустой, то уничтожаем переменную    	if (isset($_POST['pass'])){       		$pass=$_POST['pass'];       		if ($pass =='') {      		unset($pass);    }    	if (empty($login) or empty($pass)) //если пользователь не ввел логин или пароль, то выдаем ошибку и останавливаем скрипт    {         exit ("Заполните, пожалуйста, все поля!");    }     	$login = stripslashes($login);    	$login = htmlspecialchars($login);    	$pass = stripslashes($pass);    	$pass = htmlspecialchars($pass);    	$login = trim($login); //удаляем лишние пробелы    	$pass = trim($pass);	        $_SESSION["username" ] = $login;    	$_SESSION["password" ] = $pass;  }		$this->content = $this->Template('templates/test.tpl', array('text' => $text));		}}